import React, { Component } from 'react';
import './App.css';

class App extends Component {
	state = {
		todos: []
	};

	componentDidMount() {

	}

	handleconfirm = () => {
    let customer_name = document.getElementById('input_name').value;
    let barber_name = document.getElementById('input_barber').value;

    document.getElementById('input_barber').value = '';
    document.getElementById('input_name').value = '';

    if (customer_name.length == 0)
        return;

    var customer = document.createElement('span');
    var barber = document.createElement('span');
    var x_button = document.createElement('span');

    if (barber_name == ''){
        customer.classList.add('barber-wait');
        barber.classList.add('barber-wait');
        customer.classList.add('queue-element');
        barber.classList.add('queue-element');
    } else {
        customer.classList.add('barber-none');
        barber.classList.add('barber-none');
        customer.classList.add('queue-element');
        barber.classList.add('queue-element');
    }

    customer.innerHTML += customer_name;
    barber.innerHTML += barber_name;
    x_button.innerHTML += 'X';

    x_button.classList.add('x-button');
    x_button.addEventListener('click', removeCustomer);

    let queue = document.getElementById('queue');
    queue.appendChild(customer);
    queue.appendChild(barber);
    queue.appendChild(x_button);
}

removeCustomer = (event) => {
  let queue = document.getElementById('queue');
  queue.removeChild(event.target.previousElementSibling);
  queue.removeChild(event.target.previousElementSibling);
  queue.removeChild(event.target);
}

	render() {
		return (
      <div>
			<head>
    <link rel="stylesheet" type="text/css" href="css/main.css"></link>
    <link href="https://fonts.googleapis.com/css?family=Montserrat&display=swap" rel="stylesheet"></link>
    <title>Mala Fade</title>
</head>

<body class='body'>
<img class='logo' src="http://www.malafadebarbershop.com/uploads/7/9/7/9/79799084/published/9475109_1.png?1542419052" alt="logo"></img>
<div class='grid-2'>
    <div id='queue' class ='grid-queue'>
        <span class='sub-title'>Customer</span>
        <span class='sub-title'>Barber</span>
        <span></span>
    </div>
    <div class = 'customer-input'>

                <div class='request'>Please enter your name</div>
                <div class='input-field'><input type="text" id="input_name"></input></div>

        <div>
            <div class='request'>Waiting for a specific barber?</div>
            <div class='request'>If yes, enter their name</div>
            <div class='request'>If no, leave blank</div>
            <div class='input-field'><input type="text" id="input_barber"></input></div>

            <div class='button-confirm-div' onClick={this.handleconfirm()}>
                <a id='btn_submit' class='button-confirm-a'>
                    <span class='rat'>Confirm</span>
                </a>
            </div>

            <div class='sub-title'>Services</div>
            <div class='services'>Fades . 20</div>
            <div class='services'>Adult Haircut . 17</div>
            <div class='services'>Seniors . 14</div>
            <div class='services'>Children . 14</div>
            <div class='services'>Dry Shave . 12</div>
            <div class='services'>Hot Shave . 22</div>
            <div class='services'>Beard Trim . 6</div>
            <div class='services'>Beard Line-Up . 6</div>
            <div class='services'>Black Mask . 6</div>
            <div class='services'>Beard Colour . 12</div>
            <div class='services'>Hair Black Colour . 20</div>
            <div class='services'>hair Design . 10+</div>
        </div>
    </div>
</div>
</body>
</div>
		);
	}
}

export default App;
